CREATE TABLE Membership 
(MemberID int IDENTITY, 
 FirstName varchar(100) MASKED WITH (FUNCTION = 'partial(1,"XXXXXXX",0)') NULL, 
 LastName varchar(100) NOT NULL, 
 Phone varchar(12) MASKED WITH (FUNCTION = 'default()') NULL, 
 Email varchar(100) MASKED WITH (FUNCTION = 'email()') NULL); 


--Insert sample data in the table 
INSERT Membership VALUES ('Roberto', 'Tamburello', '555.123.4567', 'RTamburello@contoso.com'); 
INSERT Membership VALUES ('Janice', 'Galvin', '555.123.4568', 'JGalvin@contoso.com.co'); 
INSERT Membership VALUES ('Zheng', 'Mu', '555.123.4569', 'ZMu@contoso.net'); 
SELECT * FROM Membership; 


-- A new user is created and granted SELECT permission on the table.  
-- Queries executed as the TestUser view masked data. 

-- Connect to MASTER
CREATE LOGIN User1 WITH PASSWORD = 'teste@123';
-- Connect to DATABASE
CREATE USER User1 WITHOUT LOGIN; 
GRANT SELECT ON Membership TO User1; 


--Test user permissions 
EXECUTE AS USER = 'User1'; 
SELECT * FROM Membership; 
REVERT; 


--The following example adds a masking function to the LastName column 
ALTER TABLE Membership 
ALTER COLUMN LastName ADD MASKED WITH (FUNCTION = 'partial(2,"XXX",0)'); 


--The following example changes a masking function on the LastName column 
ALTER TABLE Membership 
ALTER COLUMN LastName varchar(100) MASKED WITH (FUNCTION = 'default()'); 


--Granting the UNMASK permission allows TestUser to see the data unmasked 
GRANT UNMASK TO User1; 
EXECUTE AS USER = 'User1'; 
SELECT * FROM Membership; 
REVERT; 


-- Removing the UNMASK permission 
REVOKE UNMASK TO User1; 


-- The following statement drops the mask on the LastName column 
-- created in the previous example 
ALTER TABLE Membership 
ALTER COLUMN LastName DROP MASKED;

--Create three user accounts that will demonstrate different access  
-- capabilities 

-- Connect to MASTER
CREATE LOGIN Manager WITH PASSWORD = 'teste@123';
-- Connect to DATABASE
CREATE USER Manager FROM LOGIN Manager; 


--Create a table to hold data 
CREATE TABLE Sales 
( 
    OrderID int, 
    SalesRep sysname, 
    Product varchar(10), 
    Qty int 
); 

--Insert data into table with six rows of data,  
--showing three orders for each sales representative 
INSERT INTO Sales VALUES (1, 'Sales1', 'Valve', 5); 
INSERT INTO Sales VALUES (2, 'Sales1', 'Wheel', 2); 
INSERT INTO Sales VALUES (3, 'Sales1', 'Valve', 4); 
INSERT INTO Sales VALUES (4, 'Sales2', 'Bracket', 2); 
INSERT INTO Sales VALUES (5, 'Sales2', 'Wheel', 5); 
INSERT INTO Sales VALUES (6, 'Sales2', 'Seat', 5); 

-- View the 6 rows in the table 
SELECT * FROM Sales; 

--Grant read access on the table to each of the users 
GRANT SELECT ON Sales TO Manager; 
GRANT SELECT ON Sales TO User1; 

--Create a new schema, and an inline table-valued function. 
--The function returns 1 when a row in the SalesRep column is the same  
--as the user executing the query (@SalesRep = USER_NAME()) or  
--if the user executing the query is the Manager user  
--(USER_NAME() = 'Manager') 
EXEC('CREATE SCHEMA Security'); 
GO 

--Select the code for creating the function and run 
CREATE FUNCTION Security.fn_securitypredicate(@SalesRep AS sysname) 
    RETURNS TABLE 
WITH SCHEMABINDING 
AS 
    RETURN SELECT 1 AS fn_securitypredicate_result 
WHERE @SalesRep = USER_NAME() OR USER_NAME() = 'User1' 

GO 

--Create a security policy adding the function as a filter predicate.  
--The state must be set to ON to enable the policy. 
CREATE SECURITY POLICY SalesFilter 
ADD FILTER PREDICATE Security.fn_securitypredicate(SalesRep) 
ON dbo.Sales 
WITH (STATE = ON); 


--Allow SELECT permissions to the fn_securitypredicate function 
GRANT SELECT ON security.fn_securitypredicate TO Manager; 
GRANT SELECT ON security.fn_securitypredicate TO User1; 

--Test the filtering predicate, by selected from the  
-- Sales table as each user 
EXECUTE AS USER = 'User1'; 
SELECT * FROM Sales; 
REVERT; 

EXECUTE AS USER = 'Manager'; 
SELECT * FROM Sales; 
REVERT; 


--Alter the security policy to disable the policy 
ALTER SECURITY POLICY SalesFilter 
WITH (STATE = OFF); 

--Connect to the SQL database to clean up resources 
DROP USER User1; 
DROP USER Manager; 


DROP SECURITY POLICY SalesFilter; 
DROP TABLE Sales; 
DROP FUNCTION Security.fn_securitypredicate; 
DROP SCHEMA Security;

